import { useState,useEffect } from "react";
import Menu from "./menu";
import exifr from "exifr";
import TagManager from "./create/list/TagManager";
import { checkedToken } from "./function/checkedToken";
import { fetchUserID } from "./function/fetchUserID";
import { fetchUserInfo } from "./community/api/fetchUserInfo";
import { createPost } from "./api/createPost";

export default function CreatePost() {
  const [isOpen, setIsOpen] = useState(true);
  const [selectedImages, setSelectedImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  
  // Ïù¥ÎØ∏ÏßÄÎ≥Ñ Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Î∞∞Ïó¥
  const [imageMeta, setImageMeta] = useState([]);
  const [imageAddress, setImageAddress] = useState([]);

  // ÌÉúÍ∑∏ Í¥ÄÎ†® ÏÉÅÌÉú Ï∂îÍ∞Ä
  const [tags, setTags] = useState([]);
  const [showInput, setShowInput] = useState(false);
  const [currentTag, setCurrentTag] = useState("");
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingTag, setEditingTag] = useState("");
  const [postText, setPostText] = useState("");
  const [titleText, setTitleText] = useState("");

  // Ïú†Ï†Ä Ï†ïÎ≥¥ ÏÉÅÌÉúÍ¥ÄÎ¶¨
  const [userName, setUsername] = useState("");
  const [name, setName] = useState("");
  const [userInfo, setUserInfo] = useState([]);
  const [formatInfo, setFormatInfo] = useState({})

  useEffect(()=>{
    const getUserInfo = async ()=>{
      await checkedToken(setUsername, setName);
    }
    getUserInfo();
    
  },[])

  useEffect(()=>{
    const getUserInfo = async ()=>{
      const res = await fetchUserInfo(name);
      setUserInfo(res);
    }
    if(userName && name){
      getUserInfo();
    }
  },[userName, name])

  // ÏÑúÎ≤ÑÎ°ú Ï†ÑÏÜ°ÌïòÍ∏∞ ÏúÑÌï¥ Í∞ÄÍ≥µÌïú Îç∞Ïù¥ÌÑ∞
  useEffect(()=>{
    if(userName && name && userInfo.length !== 0){
      setFormatInfo({
        id:userInfo[0]?.friend_id?? 0,
        name:userName,
        email:name,
        profile:userInfo[0]?.profile_image_url?? 0,
        postText:postText,
        titleText:titleText,
        address:imageAddress,
        imageData: selectedImages
      });
    }
  },[userName,name,userInfo,postText,titleText,imageAddress,selectedImages]);

  useEffect(()=>{
  },[formatInfo,postText,selectedImages]);

  const handleClose = () => {
    if (window.confirm("Í≤åÏãúÎ¨ºÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")){
      setIsOpen(false);
      window.history.back();
    };                                                                                                                                                          
  };

  async function getAddressFromCoords(lat, lng) {
    const res = await fetch(
      `https://dapi.kakao.com/v2/local/geo/coord2address.json?x=${lng}&y=${lat}`,
      {
        headers: {
          Authorization: 'KakaoAK e3edda565841b375880733ac67c7e2f8'
        }
      }
    );
    const data = await res.json();
    if (data.documents && data.documents.length > 0) {
      return data.documents[0].address.address_name;
    }           
    return null;
  }

  const handleImageSelect = async (event) => {
    // ÏÇ¨Ïö©ÏûêÍ∞Ä ÏÑ†ÌÉùÌïú ÌååÏùºÎì§ÏùÑ Í∞ÄÏ†∏ÏôÄÏÑú Î∞∞Ïó¥Î°ú Î≥ÄÌôò
    // event.target.filesÎäî FileList Í≤çÏ≤¥Ïù¥Í∏∞ ÎïåÎ¨∏Ïóê Î∞∞Ïó¥Î°ú Î∞îÍøîÏ§å
    const files = Array.from(event.target.files);

    // Í∞Å ÌååÏùºÏùÑ FileReaderÎ°ú ÏùΩÏñ¥Îì§Ïù¥Í∏∞ ÏúÑÌïú Promise Î∞∞Ïó¥ ÏÉùÏÑ±
    const readers = files.map((file) => {
      return new Promise((resolve) => {
        // Î∏åÎùºÏö∞Ï†Ä ÎÇ¥Ïû• Í∞ùÏ≤¥ FileReader ÏÉùÏÑ±
        const reader = new FileReader();
        reader.onload = async (e) => {
          // exifrÎ°ú ÏúÑÏπòÏ†ïÎ≥¥ Ï∂îÏ∂ú
          const gps = await exifr.gps(file);
          let address = null;
          if (gps && gps.latitude && gps.longitude) {
            address = await getAddressFromCoords(gps.latitude, gps.longitude);
          }
          resolve({ src: e.target.result, gps, address });
        };
        reader.readAsDataURL(file);
      });
    });
    Promise.all(readers).then((images) => {

      // if(images.some(img => !img.address)){
