import Masonry from 'react-masonry-css';
import { useEffect, useRef, useState } from 'react';

export default function Gallery(props) {

  // Í∞§Îü¨Î¶¨Î•º ÎßàÏö¥Ìä∏Ìïú ÌéòÏù¥ÏßÄÏùò src
  const { src } = props;

  const breakpointColumnsObj = {
    default: src === "profile" ? 3 : 5,
    1536: src === "profile" ? 3 : 5,
    1280: src === "profile" ? 2 : 4,
    1024: src === "profile" ? 1 : 3,
    768: src === "profile" ? 1 : 2,
    640: src === "profile" ? 1 : 1,
  };

  const [items, setItems] = useState([...Array(30).keys()]);
  const observerRef = useRef(null);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const target = observerRef.current; // üí° Ïó¨Í∏∞Ïóê Ï†ÄÏû•
    if (!target) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setItems((prev) =>
            prev.concat(Array.from({ length: 15 }, (_, i) => prev.length + i))
          );
        }
      },
      {
        root: null,
        rootMargin: '0px',
        threshold: 1.0,
      }
    );

    observer.observe(target);

    return () => {
      observer.unobserve(target); // ‚úÖ ÏïàÏ†ïÏ†ÅÏúºÎ°ú cleanup
    };
  }, [items, windowWidth]); // ‚Üê Ïù¥Í±¥ Ïú†ÏßÄÌï¥ÎèÑ Îê®

  return (
    <>
      <div className={` h-[100%] sm:h-[100%] sm:overflow-y-auto overflow-x-hidden sm:p-4 sm:pr-8 ${src === "profile" ? "sm:w-[50%]" : ""}`}>
        {/* ‚úÖ PCÌôîÎ©¥ Î¨¥ÌïúÏä§ÌÅ¨Î°§ */}
        <Masonry
          breakpointCols={breakpointColumnsObj}
          className="hidden sm:flex gap-5"
          columnClassName="flex flex-col gap-5"
        >
          {items.map((i) => (
            <div
              key={i}
              className={`bg-red-500 rounded-2xl text-white text-center ${i % 3 === 1
                  ? 'h-[500px]'
                  : i % 3 === 2
                    ? 'h-[400px]'
                    : 'h-[300px]'
                }`}
            >
              <img
                className="w-full h-full object-cover rounded-2xl"
                src={`/images/Ïù¥ÎØ∏ÏßÄ (${i + 1}).jpg`}
                alt="Ïù¥ÎØ∏ÏßÄ"
              ></img>
            </div>
          ))}
          {/* üëá Î¨¥Ìïú Ïä§ÌÅ¨Î°§ Í∞êÏãú ÎåÄÏÉÅ ÏöîÏÜå */}
          <div ref={observerRef} className="h-]" />
        </Masonry>

        {/* ‚úÖ Î™®Î∞îÏùºÌôîÎ©¥ Í≥†Ï†ï Î¶¨Ïä§Ìä∏ */}
        <div className="block sm:hidden h-full overflow-y-auto overflow-x-hidden">
          {Array.from({ length: 100 }, (_, i) => (
            <div key={i}>
              <div className="bg-gray-200 h-[500px] w-screen ">
                <img
                  className="w-full h-full object-cover"
                  src={`/images/Ïù¥ÎØ∏ÏßÄ (${i + 1}).jpg`}
                  alt="Ïù¥ÎØ∏ÏßÄ"
                ></img>
              </div>
              <div className=" h-[23vh] w-screen"></div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
