//client/src/componant/community/chatroom.js

import { useEffect, useState, useMemo, useRef } from "react";
import Conversation from "./conversation";
import { insertDateHeaders, formatDateKorean } from "./utilities/dateUtils";
import { CheckedCurrentMemberButton, CommunityButtons } from "./ui/button";
import { fetchMessageAPI, fetchMessageReadAPI } from "./api/fetchMessageAPI";
import io from "socket.io-client";
import { changeChatRoomColor, changeInterestColor } from "../profile/utilities/interestColor";

export default function Chatroom(props) {

    // Ï†ÑÎã¨Îêú ÎåÄÌôîÎ∞©Í≥º Ìï¥ÎãπÌïòÎäî Ïú†Ï†ÄÏ†ïÎ≥¥
    const { chattingList, selectedList, flexCenter } = props;
    // Î™®Îã¨ÏùÑ Í¥ÄÎ¶¨ÌïòÍ∏∞ ÏúÑÌïú props
    const {friendList, blockedList, setActionList} = props;
    // Ï±ÑÌåÖ ÏÜ°Ïã†ÏùÑ ÏúÑÌïú props
    const {userInfo} = props;

    // ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Ìïú Ïú†Ï†Ä
    const {userName} = props;

    // ÎåÄÌôîÎ™©Î°ù ÏµúÏã†Ìôî props
    const {setFetchChatList} = props;

    // ÏÑ†ÌÉù Îêú Î∞© ÏÉÅÌÉúÍ¥ÄÎ¶¨
    const [chatroom, setChatroom] = useState("");

    // ÏÑ†ÌÉù Îêú Î∞© ÌÖåÎßà ÏÉÅÌÉúÍ¥ÄÎ¶¨
    const [chatroomTheme, setChatroomTheme] = useState("");

    // ÏÑ†ÌÉù Îêú Î∞© Î∞©Ïû• ÌîÑÎ°úÌïÑ ÏÉÅÌÉúÍ¥ÄÎ¶¨
    const [chatroomProfile, setChatroomProfile] = useState("");

    // ÏÑ†ÌÉù Îêú Î∞© ÏïÑÏù¥Îîî
    const [chatroomID, setChatroomID] = useState("");

    // Ï∞∏Ïó¨Ïù∏Ïõê/ÎÇòÍ∞ÄÍ∏∞ Î≤ÑÌäº ÏÉÅÌÉúÍ¥ÄÎ¶¨ Ïä§ÌÖåÏù¥Ìä∏
    const [checkedMember, setCheckedMember] = useState(false);

    // ÎÇòÍ∞ÄÍ∏∞ Î≤ÑÌäº ÏÉÅÌÉúÍ¥ÄÎ¶¨ Ïä§ÌÖåÏù¥Ìä∏
    const [checkedAction, setCheckedAction] = useState({});

    // Ï±ÑÌåÖÏóê ÏûÖÎ†•Ìïú ÎÇ¥Ïö© ÏÉÅÌÉúÍ¥ÄÎ¶¨
    const [messageText, setMessageText] = useState("");

    // ÌòÑÏû¨ Ï±ÑÌåÖÏóê Î∞©Ïóê ÏûÖÏû•Ìïú Ïú†Ï†Ä
    const [chatroomUser,setChatRoomUser] = useState([]);


    // selectedList Í∞íÏù¥ Î≥ÄÎèôÏù¥ ÏûàÏùÑÎïåÎßå Ïã§Ìñâ
    useEffect(() => {
        const selectedFriend = chattingList.find((friend) => friend.chat_room_id === selectedList);
        if (selectedFriend) {
            setChatroom(selectedFriend.owner_name);
            setChatroomProfile(selectedFriend.profile_image_url);
            setChatroomTheme(selectedFriend.theme);
            setChatroomID(selectedFriend.chat_room_id);
            console.log(selectedFriend);
        }
    }, [selectedList, chattingList]);

    // Ï±ÑÌåÖÎÇ¥Ïö© ÏÉÅÌÉúÍ¥ÄÎ¶¨
    const [messaging, setMessaging] = useState([]);

    // Ï∞∏Ïó¨Ïù∏Ïõê ÏÉÅÌÉúÍ¥ÄÎ¶¨
    const [currentMembers, setCurrentMembers] = useState([]);

    useEffect(() => {
    const getMessage = async () => {
        try {
            // Î©îÏãúÏßÄ Î∂àÎü¨Ïò§Í∏∞
            const rawMessages = await fetchMessageAPI(chatroomID, "chatmessage");
            const currentMembersList = await fetchMessageAPI(chatroomID, "currentMember");

            // ÏùΩÏùå Ïàò Í≥ÑÏÇ∞
            const enrichedMessages = await Promise.all(rawMessages.map(async (msg) => {
                const readResult = await fetchMessageReadAPI(userInfo.friend_id, msg.message_id);
                const readCount = readResult[0]?.messageReadCount || 0;
                return {
                    ...msg,
                    messageReads: currentMembersList.length - readCount
                };
            }));

            setCurrentMembers(currentMembersList);
            setMessaging(enrichedMessages);
        } catch (error) {
            console.error("Î©îÏÑ∏ÏßÄ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
        }
    };

    if (chatroomID) getMessage();
}, [chatroomID]);

    // ÎÇ†Ïßú Íµ¨Î∂Ñ Î°úÏßÅ Ï∂îÍ∞Ä
    const formattedMessages = useMemo(()=>{
        return insertDateHeaders(messaging);
    },[messaging]); 

    const socketRef = useRef();

    // socketÏóê Ïó∞Í≤∞ÌïòÎäî Î°úÏßÅ
    useEffect(() => {
        // ÏÜåÏºì ÏÉùÏÑ±
        if (!socketRef.current && userInfo !== undefined) {
            socketRef.current = io("http://localhost:5000", {
                transports: ["websocket"], // ÏïàÏ†ïÏ†ÅÏù∏ Ïó∞Í≤∞ Î∞©Ïãù ÏÇ¨Ïö©
                autoConnect: false,        // ÏßÅÏ†ë connect() Ìò∏Ï∂ú
                query:{
                    userID: userInfo.friend_id, 
                }
            });
            console.log("userInfo:",userInfo.friend_id);
        }

        const socket = socketRef.current;


        if (!chatroomID) return;

        console.log("[ÏÜåÏºì] Ïó∞Í≤∞ ÏãúÎèÑ (connect) - Î∞© ID:", chatroomID);
        socket.connect();

        socket.on("connect", () => {
            console.log("[ÏÜåÏºì] Ïó∞Í≤∞ ÏÑ±Í≥µ:", socket.id);
            socket.emit("join_room", chatroomID);
            console.log("[ÏÜåÏºì] join_room emit:", chatroomID);
        });

        // ÌòÑÏû¨ Ï±ÑÌåÖÏ§ëÏù∏ Ïú†Ï†ÄÎ•º ÏÉÅÌÉúÏóê Ï†ÄÏû•
        socket.on("room_user_count", (data) =>{setChatRoomUser(data.connectedUserIds)} );

        // Î©îÏÑ∏ÏßÄ ÏàòÏã†ÌïòÎäî Î∂ÄÎ∂Ñ
        socket.on("receive_message", (msg) => {
            console.log("[ÏÜåÏºì] receive_message ÏàòÏã†:", msg);
            setMessaging((prev) => [...prev, msg]);
            
            // Î≥∏Ïù∏Ïù¥ Î≥¥ÎÇ∏ Î©îÏÑ∏ÏßÄÍ∞Ä ÏïÑÎãàÎ©¥, ÏùΩÏùå Ï≤òÎ¶¨ ÏÑúÎ≤ÑÎ°ú ÏïåÎ¶º
            if(msg.sender_id !== userInfo.friend_id){
                socket.emit("message_read",{
                    message_id: msg.message_id,
                    user_id: userInfo.friend_id
                });
            }
        });

        socket.on("disconnect", () => {
            console.log("[ÏÜåÏºì] Ïó∞Í≤∞ Ìï¥Ï†úÎê®");
        });

        socket.on("connect_error", (err) => {
            console.error("[ÏÜåÏºì] Ïó∞Í≤∞ Ïò§Î•ò:", err.message);
        });

        return () => {
            console.log("[ÏÜåÏºì] leave_room emit:", chatroomID);
            socket.emit("leave_room", chatroomID);
            socket.off("receive_message");
            socket.disconnect();
            console.log("[ÏÜåÏºì] Ïó∞Í≤∞ Ìï¥Ï†ú (disconnect)");
        };
    }, [chatroomID,userInfo]);

    // Î©îÏÑ∏ÏßÄ ÏÜ°Ïã†Ìï®Ïàò
    const handleSendMessage = () => {
        if (!messageText.trim()) return;

        const socket = socketRef.current;

        if (!socket || !socket.connected) {
            console.warn("ÏÜåÏºì Ïó∞Í≤∞ÎêòÏßÄ ÏïäÏùå. Î©îÏãúÏßÄ Ï†ÑÏÜ° Î∂àÍ∞Ä.");
            return;
        }

        const now = new Date();
        const kst = new Date(now.getTime() + 9 * 60 * 60 * 1000); // 9ÏãúÍ∞Ñ ÎçîÌï®
        const newMessage = {
            chat_room_id: chatroomID,
            sender_id: userInfo.friend_id,
            profile_image_url: userInfo.profile_image_url,
            name: userInfo.name,
            message: messageText,
            datetime: kst.toISOString().slice(0,19).replace("T", " "),
            messageReads: currentMembers.length - chatroomUser.length,
        };

        socket.emit("send_message", newMessage);
        // setMessaging((prev) => [...prev, newMessage]);
        setMessageText("");
    };

    // Î©îÏÑ∏ÏßÄ Ï†ÑÏÜ° ÌõÑ Ïä§ÌÅ¨Î°§
    const messageEndRef = useRef(null);

    useEffect(()=> {
        if(messageEndRef.current){
            messageEndRef.current.scrollIntoView({block: "end"});
        }
        setFetchChatList(messaging);
    },[messaging]);

    // ineterest Color Î≥ÄÏàò
    const interestColor = changeInterestColor(chatroomTheme.split("&")[0].trim());
    // chatRoom Color Î≥ÄÏàò
    const chatRoomColor = changeChatRoomColor(chatroomTheme.split("&")[0].trim());

    return (
        <>
            {/* ÏÑ†ÌÉùÎêú ÎåÄÌôîÎ∞©Ïù¥ ÏóÜÏùÑ Ïãú Ï°∞Í±¥Î¨∏ */}
            {chatroom === "" ? (
                <div className={`${flexCenter} w-full h-full text-black text-opacity-50`}>
                    Î∂àÎü¨Ïò® ÎåÄÌôîÎ∞©Ïù¥ ÏóÜÏäµÎãàÎã§. Î™©Î°ùÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.
                </div>
            ) : (
                // ÎåÄÌôîÎ∞© ÏÑ†ÌÉù Ïãú
                <>
                    {/* ÏÑ†ÌÉùÎêú ÎåÄÌôîÎ∞© Ìó§ÎçîÏòÅÏó≠ */}
                    <div className={`flex items-center w-full h-[70px] border-b border-solid border-gary-300 bg-white rounded-t-3xl`}>
                        {/* ÏÑ†ÌÉùÎêú ÎåÄÌôîÎ∞© Ìó§ÎçîÏöîÏÜå */}
                        
                        <img src={`${chatroomProfile}`} alt="ÎØ∏ÎãàÌîÑÎ°úÌïÑ" className={`w-[40px] h-[40px] rounded-[50%] ml-4`} />
                        <span className={`ml-4`}>{chatroom}</span>
                        <span className={`ml-4 p-[0.15rem] px-3 text-[0.8rem] rounded-3xl ${interestColor} font-sans font-bold`}>
                            {chatroomTheme}
                        </span>
                        <div
                            onClick={() => {
                                setCheckedMember(true);
                            }}
                            className={`${flexCenter} ml-auto bg-black hover:bg-blue-500 transition-colors duration-300 text-white rounded-md w-[80px] h-[30px] text-[0.8rem] font-bold cursor-pointer`}
                        >
                            Ï∞∏Ïó¨Ïù∏Ïõê
                            {/* Ï∞∏Ïó¨Ïù∏Ïõê Î≤ÑÌäº ÌÅ¥Î¶≠ Î™®Îã¨ */}
                            <CheckedCurrentMemberButton
                                friendList = {friendList}
                                blockedList = {blockedList}
                                checkedMember={checkedMember}
                                setCheckedMember={setCheckedMember}
                                chattingList={currentMembers}
                                setActionList = {setActionList}
                                userID = {userInfo.friend_id}
                            />
                        </div>

                        <div
                            onClick={() => {
                                setCheckedAction(prev => ({ ...prev, [chattingList.id]: true }));
                            }}
                            className={`${flexCenter} mx-2 mr-4 bg-black hover:bg-blue-500 transition-colors duration-300 text-white rounded-md w-[80px] h-[30px] text-[0.8rem] font-bold cursor-pointer`}
                        >
                            Î∞© ÎÇòÍ∞ÄÍ∏∞
                            {/* ÎÇòÍ∞ÄÍ∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ Î™®Îã¨ */}
                            <CommunityButtons
                                checkedAction={checkedAction}
                                setCheckedAction={setCheckedAction}
                                title={`${chatroom}ÎãòÏùò ÎåÄÌôîÎ∞©`}
                                message={"Ï†ïÎßê ÎÇòÍ∞ÄÏãúÍ≤†ÏäµÎãàÍπå?"}
                                action={"ÎÇòÍ∞ÄÍ∏∞"}
                            />
                        </div>
                    </div>

                    {/* ÏÑ†ÌÉùÎêú ÎåÄÌôîÎ∞© Ï±ÑÌåÖÏòÅÏó≠ */}
                    <div className={`flex flex-col w-full h-[calc(100%_-_140px)] overflow-y-auto ${chatRoomColor}`}>
                        {formattedMessages.map((item, index) => {
                            if (item.type === "date-stamp") {
                                return (
                                    <div key={`date-${index}`} className="flex justify-center text-gray-500 font-bold my-4 font-sans">
                                        <div className={`shadow-md px-2 rounded-2xl bg-white`}>üìÖ {formatDateKorean(item.date)}</div>
                                    </div>
                                );
                            }
                            return (
                                <>
                                    <Conversation 
                                        message={item}
                                        currentUserName={userName}
                                        friendList = {friendList}
                                        blockedList = {blockedList}
                                        chattingList = {messaging}
                                        setActionList = {setActionList}
                                        currentMembers = {currentMembers.length}
                                        userID = {userInfo.friend_id}
                                        messageReads = {item.messageReads}
                                    />
                                    {formattedMessages.length-1 === index && 
                                    <div ref={messageEndRef} className={`h-[10px]`}></div>
                                    }
                                </>                                   
                            );
                        })}
                    </div>

                    {/* Ï±ÑÌåÖÎÇ¥Ïö© ÏûÖÎ†• ÏòÅÏó≠ */}
                    <div className={` flex items-center w-full h-[70px] border-t-[2px] border-solid border-gray-200 bg-white rounded-b-3xl`}>
                        <textarea
                        value={messageText}
                        onKeyDown={(e) => {
                            if(e.key === 'Enter'){
                                if(e.shiftKey){
                                    return;
                                }else{
                                    e.preventDefault(); // Í∏∞Î≥∏ Enter Ï§ÑÎ∞îÍøà Î∞©ÏßÄ
                                    handleSendMessage();
                                }
                            }
                        }} 
                        onChange={(e)=> {setMessageText(e.target.value); console.log(messageText)}}
                        placeholder="ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî." className={`focus:outline-none resize-none pt-5 pl-5 w-[calc(100%_-_120px)] rounded-3xl`}></textarea>
                        <img alt="ÌÅ¥Î¶Ω" src="/images/ÌÅ¥Î¶Ω.png" className={`w-[20px] h-[20px] mr-2`} />
                        <div 
                        onClick={handleSendMessage}
                        className={`w-[70px] h-[35px] bg-black hover:bg-blue-500 transition-colors duration-300 cursor-pointer rounded-lg flex justify-center items-center`}>
                            <img alt="Î≥¥ÎÇ¥Í∏∞" src="/images/Î≥¥ÎÇ¥Í∏∞.png" className={`w-[20px] h-[20px]`} />
                        </div>
                    </div>
                </>
            )}
        </>
    );
}
