// client/createList.js

import { useEffect, useState } from "react";
import Menu from "./menu.js";
import TopButtons from "./create/list/TopButtons.js";
import IsPlanned from "./create/list/IsPlanned.js";
import TagManager from "./create/list/TagManager.js";
import TitleAndSelectInterest from "./create/list/TitleAndSelectInterest.js";
import DayList from "./create/list/DayList.js";
import axios from "axios";
import { checkedToken } from "./function/checkedToken.js";
import { fetchUserID } from "./function/fetchUserID.js";
import { fetchUserInfo } from "./community/api/fetchUserInfo.js";

export default function CreateList() {


  const [name,setName] = useState("");
  const [userName, setUsername] = useState("");
  const [userID, setUserID] = useState("");
  const [userInfo, setUserInfo] = useState("");

  useEffect(()=>{
    const getUserInfo = async()=>{
      try{
        await checkedToken(setUsername, setName);
      }catch(error){
        console.error(error);
      }
    }
    getUserInfo();
  },[])

  useEffect(()=>{
    const getUserID = async()=>{
      try{
        const id = await fetchUserID(name);
        const userInfo = await fetchUserInfo(name);
        setUserID(id);
        setUserInfo(userInfo);
      }catch(error){
        console.error(error);
      }
    }
    getUserID();
  },[name,userName]);

  useEffect(()=>{
    console.log(userID);
  },[userID])

  // Í≥ÑÌöçÌòï, ÎπÑÍ≥ÑÌöçÌòï ÏÉÅÌÉúÍ¥ÄÎ¶¨ Ïä§ÌÖåÏù¥Ìä∏
  const [isPlanned, setIsPlanned] = useState(true);
  // Ï†úÎ™©Í≥º ÏÜåÍ∞úÍ∏Ä ÏÉÅÌÉú Ï∂îÍ∞Ä
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  // ÎßàÍ∞ê ÎÇ†Ïßú
  const [endDate, setEndDate] = useState("");
  // ÎßåÎÇ† Ïû•ÏÜå
  const [meetPlace, setMeetPlace] = useState("");
  // ÏòàÏÇ∞
  const [budget, setBudget] = useState("");
  // Ïã§Ìñâ Í∏∞Í∞Ñ
  const [periodStartDate, setPeriodStartDate] = useState("");
  const [periodEndDate, setPeriodEndDate] = useState("");

  //ÏÑ†ÌÉùÎêú Í¥ÄÏã¨ÏÇ¨ ÏÉÅÌÉú
  const [selectedInterest, setSelectedInterest] = useState("");
  // Í¥ÄÏã¨ÏÇ¨ ÏÑ†ÌÉù Ìï≠Î™©Î≥ÄÏàò
  const selectInterest = [
    "Ïó¨Ìñâ & ÌÉêÌóò",
    "ÎèÑÏ†Ñ & Ïñ¥ÎìúÎ≤§Ï≤ò",
    "ÏûêÍ∏∞Í≥ÑÎ∞ú & ÍµêÏú°",
    "Í±¥Í∞ï & Ïö¥Îèô",
    "Î¨∏Ìôî & ÏòàÏà†",
    "ÏöîÎ¶¨ & ÎØ∏Ïãù",
    "Î¥âÏÇ¨ & ÎÇòÎàî",
    "Ï∑®ÎØ∏ & ÎùºÏù¥ÌîÑ",
    "Í∏∞ÌÉÄ",
  ];

  // ÌÉúÍ∑∏ Î™©Î°ù
  const [tags, setTags] = useState([]);
  // ÏûÖÎ†• ÌïÑÎìú ÌëúÏãú Ïó¨Î∂Ä
  const [showInput, setShowInput] = useState(false);
  // ÌòÑÏû¨ ÏûÖÎ†•Îêú ÌÉúÍ∑∏
  const [currentTag, setCurrentTag] = useState("");
  // ÏàòÏ†ï Ï§ëÏù∏ ÌÉúÍ∑∏Ïùò Ïù∏Îç±Ïä§
  const [editingIndex, setEditingIndex] = useState(null);
  // ÏàòÏ†ï Ï§ëÏù∏ ÌÉúÍ∑∏ Í∞í
  const [editingTag, setEditingTag] = useState("");

  // ÌòºÏûêÌïòÍ∏∞, Í∞ôÏù¥ÌïòÍ∏∞ ÏÉÅÌÉúÍ¥ÄÎ¶¨ Ïä§ÌÖåÏù¥Ìä∏
  const [Group, setGroup] = useState(true);
  // ÎåÄÎ©¥, ÎπÑÎåÄÎ©¥ Ïó¨Î∂Ä ÏÉÅÌÉúÍ¥ÄÎ¶¨ Ïä§ÌÖåÏù¥Ìä∏
  const [Offline, setOffline] = useState(true);

  // ÏùºÏ∞® Î™©Î°ù Ïä§ÌÉúÏù¥Ìä∏
  const [days, setDays] = useState(["1ÏùºÏ∞®"]);
  // Îì±Î°ùÎêú Ìï≠Î™© ÏÉÅÌÉú
  const [registeredItems, setRegisteredItems] = useState({});

  // ÌÉúÍ∑∏ Ï∂îÍ∞Ä Ìï®Ïàò
  const handleAddTag = () => {
    if (currentTag.trim() !== "") {
      // Ï†úÎ™©Í≥º Í∏ÄÏóêÏÑú Îã®Ïñ¥ Ï∂îÏ∂ú
      const titleWords = title.split(/\s+/).filter((word) => word.length > 1);
      const textWords = text.split(/\s+/).filter((word) => word.length > 1);
      // ÌÉúÍ∑∏Î°ú ÏÇ¨Ïö©Ìï†Ïàò ÏûàÎäî Îã®Ïñ¥ Î™©Î°ù ÏÉùÏÑ±
      const availableWords = [...new Set([...titleWords, ...textWords])];

      // ÏïûÏóê #Ïù¥ Î∂ôÏòÄÏúºÎ©¥ ÏûêÎ•¥Í≥†, ÏóÜÏúºÎ©¥ Í∑∏ÎûòÎ°ú ÎπÑÍµê
      const tagWithoutHash = currentTag.startsWith("#")
        ? currentTag.slice(1)
        : currentTag;
      // ÏûÖÎ†•Îêú ÌÉúÍ∑∏Îã§ Ï†úÎ™©Ïù¥ÎÇò Í∏ÄÏóê Ìè¨Ìï®Îêú Îã®Ïñ¥Ïù∏ÏßÄ ÌôïÏù∏
      if (!availableWords.some((word) => word.includes(tagWithoutHash))) {
        alert("ÌÉúÍ∑∏Îäî Ï†úÎ™©Ïù¥ÎÇò Í∏ÄÏóê Ìè¨Ìï®Îêú Îã®Ïñ¥Îßå ÏÇ¨Ïö©Ìï† Ïàò ÏûàÏäµÎãàÎã§.");
        // ÏûòÎ™ªÎêú ÌÉúÍ∑∏ ÏûÖÎ†•Ïãú ÏûÖÎ†•Í∞í Ï¥àÍ∏∞Ìôî
        setCurrentTag("");
        // ÏûòÎ™ªÎêú ÌÉúÍ∑∏ ÏûÖÎ†•Ïãú ÏûÖÎ†• ÌïÑÎìú Ïà®ÍπÄ
        setShowInput(false);
        return;
      }

      // ÌÉúÍ∑∏ ÏïûÏóê #Ïù¥ ÏóÜÏúºÎ©¥ Ï∂îÍ∞Ä
      const formattedTag = currentTag.startsWith("#")
        ? currentTag.trim()
        : `#${currentTag.trim()}`;

      setTags([...tags, formattedTag]);
      // ÏûÖÎ†• ÌïÑÎìú Ï¥àÍ∏∞Ìôî
      setCurrentTag("");
      // ÏûÖÎ†• ÌïÑÎìú Ïà®Í∏∞Í∏∞
      setShowInput(false);
    }
  };

  // ÌÉúÍ∑∏ ÏàòÏ†ï ÏôÑÎ£å Ìï®Ïàò
  const handleEditTag = () => {
    if (editingTag.trim() !== "") {
      // Ï†úÎ™©Í≥º Í∏ÄÏóêÏÑú Îã®Ïñ¥ Ï∂îÏ∂ú
      const titleWords = title.split(/\s+/).filter((word) => word.length > 1);
      const textWords = text.split(/\s+/).filter((word) => word.length > 1);
      const availableWords = [...new Set([...titleWords, ...textWords])];

      // ÏàòÏ†ïÎêú ÌÉúÍ∑∏Í∞Ä ÏïûÏóê #Î∂ôÏñ¥ÏûàÏúºÎ©¥ ÏûêÎ•¥Í≥†, ÏóÜÏúºÎ©¥ Í∑∏ÎåÄÎ°ú ÎπÑÍµê
      const tagWithoutHash = editingTag.startsWith("#")
        ? editingTag.slice(1)
        : editingTag;
      // Ìè¨Ìï®Îêú Îã®Ïñ¥Í∞Ä ÏóÜÎã§Î©¥ alertÏ∞Ω Ïã§Ìñâ
      if (!availableWords.some((word) => word.includes(tagWithoutHash))) {
        alert("ÌÉúÍ∑∏Îäî Ï†úÎ™©Ïù¥ÎÇò Í∏ÄÏóê Ìè¨Ìï®Îêú Îã®Ïñ¥Îßå ÏÇ¨Ïö©Ìï† Ïàò ÏûàÏäµÎãàÎã§.");
        return;
      }

      const formattedTag = editingTag.startsWith("#")
        ? editingTag.trim()
        : `#${editingTag.trim()}`;

      const updatedTags = [...tags];
      // ÏàòÏ†ïÎêú ÌÉúÍ∑∏ Ï†ÄÏû•
      updatedTags[editingIndex] = formattedTag;
      setTags(updatedTags);
    }
    // ÏàòÏ†ï ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
    setEditingIndex(null);
    // ÏàòÏ†ï ÌïÑÎìú Ï¥àÍ∏∞Ìôî
    setEditingTag("");
  };

  // Ï†úÎ™©Ïù¥ÎÇò Í∏Ä ÏàòÏ†ï Ìï†ÎïåÎßàÎã§ ÌÉúÍ∑∏ Í≤ÄÏÇ¨
  useEffect(() => {
    const titleWords = title.split(/\s+/).filter((word) => word.length > 1);
    const textWords = text.split(/\s+/).filter((word) => word.length > 1);
    const availableWords = [...new Set([...titleWords, textWords])];

    // ÌÉúÍ∑∏Îì§ Ï§ëÏóêÏÑú Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÌÉúÍ∑∏ Ï†úÍ±∞
    const validTags = tags.filter((tag) => {
      const tagWithoutHash = tag.startsWith("#") ? tag.slice(1) : tag;
      return !availableWords.some((word) => word.includes(tagWithoutHash));
    });
    // Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÌÉúÍ∑∏Í∞Ä ÏûàÎã§Î©¥ ÌÉúÍ∑∏ Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏
    if (validTags.length !== tags.length) {
      setTags(validTags);
    }
  }, [title, text]);

  // ÌÉúÍ∑∏ ÏÇ≠Ï†ú Ìï®Ïàò
  const handleDeleteTag = (index) => {
    // Ìï¥Îãπ Ïù∏Îç±Ïä§Ïùò ÌÉúÍ∑∏ Ï†úÍ±∞
    const updatedTags = tags.filter((_, i) => i !== index); // ÏÇ≠Ï†úÌï† ÌÉúÍ∑∏Îßå ÏÇ≠Ï†ú, ÎÇòÎ®∏ÏßÄ ÎÇ®ÍπÄ
    setTags(updatedTags);
  };

  // ÏóîÌÑ∞ ÌÇ§Î°ú ÌÉúÍ∑∏ Ï∂îÍ∞Ä
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleAddTag();
    }
  };

  // AI Ï∂îÏ≤ú Í≤∞Í≥º ÏûêÎèô Ï†ÅÏö©
  useEffect(() => {
    const aiRecommendedItems = localStorage.getItem('aiRecommendedItems');
    const aiRecommendedTitle = localStorage.getItem('aiRecommendedTitle');
    const aiRecommendedDescription = localStorage.getItem('aiRecommendedDescription');
    
    if (aiRecommendedItems) {
      try {
        const parsedItems = JSON.parse(aiRecommendedItems);
        
        // ÏùºÏ∞® ÏàòÏóê ÎßûÍ≤å days Î∞∞Ïó¥ ÏóÖÎç∞Ïù¥Ìä∏
        const dayKeys = Object.keys(parsedItems);
        if (dayKeys.length > 0) {
          setDays(dayKeys);
        }
        
        // AI Ï∂îÏ≤ú Ìï≠Î™©Îì§ÏùÑ registeredItemsÏóê Ï†ÅÏö©
        setRegisteredItems(parsedItems);
        
        // Ï†úÎ™©Í≥º ÏÑ§Î™ÖÎèÑ ÏûêÎèôÏúºÎ°ú ÏÑ§Ï†ï
        if (aiRecommendedTitle) {
          setTitle(aiRecommendedTitle);
        }
        if (aiRecommendedDescription) {
          setText(aiRecommendedDescription);
        }
        
        // localStorageÏóêÏÑú Îç∞Ïù¥ÌÑ∞ ÏÇ≠Ï†ú (Ìïú Î≤àÎßå Ï†ÅÏö©ÎêòÎèÑÎ°ù)
        localStorage.removeItem('aiRecommendedItems');
        localStorage.removeItem('aiRecommendedTitle');
        localStorage.removeItem('aiRecommendedDescription');
        
        console.log('AI Ï∂îÏ≤ú Í≤∞Í≥ºÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†ÅÏö©ÎêòÏóàÏäµÎãàÎã§:', parsedItems);
      } catch (error) {
        console.error('AI Ï∂îÏ≤ú Í≤∞Í≥º Ï†ÅÏö© Ï§ë Ïò§Î•ò:', error);
        alert('AI Ï∂îÏ≤ú Í≤∞Í≥º Ï†ÅÏö© Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    }
  }, []);

  // Îì±Î°ùÎ≤ÑÌäº Î°úÏßÅ
  const handleSubmit = async () => {
    try {
      if (!title.trim()) {
        alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî");
        return;
      }
      if (!text.trim()) {
        alert("ÏÜåÍ∞úÍ∏ÄÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî");
        return;
      }
      if (!selectedInterest) {
        alert("ÌÖåÎßàÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî");
        return;
      }

      // ---ÏùºÏ∞®Î≥Ñ Ìï≠Î™© Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨---
      const hasValidItems = days.every((day, index) => {
        const dayItems = registeredItems[day] || []; // Í∞Å ÏùºÏ∞®Ïóê Îì±Î°úÎêú Ìï≠Î™©Îì§
        if (dayItems.length === 0) {
          alert(
            `${
              index + 1
            }ÏùºÏ∞®Ïóê ÏµúÏÜå ÌïòÎÇòÏù¥ÏÉÅÏùò Ìï≠Î™©(Ïù¥ÎØ∏ÏßÄ ÎòêÎäî Ïû•ÏÜå)Î•º Îì±Î°ùÌï¥Ï£ºÏÑ∏Ïöî`
          );
          return false;
        }
        return true;
      });

      
      // Ïú†Ìö®ÌïòÏßÄ ÏïäÏúºÎ©¥ ÏïÑÎûò ÏΩîÎìú Ïã§ÌñâÌïòÏßÄ ÏïäÏùå
      if (!hasValidItems) return;

      // Í≥ÑÌöçÌòïÏùº Í≤ΩÏö∞ÏóêÎßå
      if(isPlanned){
        if(!endDate){
          alert("ÎßàÍ∞ê ÎÇ†ÏßúÎ•º ÏÑ§Ï†ïÌï¥Ï£ºÏÑ∏Ïöî üìÖ");
          return;
        }
        if(!meetPlace){
          alert("ÎßåÎÇ† Ïû•ÏÜåÎ•º ÏÑ§Ï†ïÌó§Ï£ºÏÑ∏Ïöî üìç");
          return;
        }
        if(!budget){
          alert("ÏòàÏÇ∞ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî üí∞");
          return;
        }
        if(!periodStartDate || !periodEndDate){
          alert("Ïã§Ìñâ Í∏∞Í∞ÑÏùÑ ÏÑ§Ï†ïÌï¥Ï£ºÏÑ∏Ïöî üìÜ");
          return;
        }  
      }

      // ---ÏÑúÎ≤ÑÏóê Î≥¥ÎÇº Îç∞Ïù¥ÌÑ∞ Íµ¨ÏÑ±---
      const listData = {
        userID:userID[0].id,
        title: title.trim(),
        description: text.trim(),
        isPlanned,
        isGroup: Group,
        maxParticipants: Group
          ? parseInt(document.querySelector("select").value)
          : 1,
        isOffline: Offline,
        interest: selectedInterest,
        days: days.map((day, index) => ({
          dayNumber: index + 1,
          // Í∞Å ÎÇ†ÏßúÏóê Îì±Î°ùÎêú Ìï≠Î™©Îì§(ÏÇ¨ÏßÑ, Ïû•ÏÜå)
          items: registeredItems[day] || [],
        })),
        // # Ï†úÍ±∞
        tags: tags.map((tag) => tag.replace(/^#/, "")),
        // Í≥ÑÌöçÌòïÏùº ÎïåÎßå Ï∂îÍ∞Ä ÌïÑÎìúÎì§ Ìè¨Ìï®
        ...(isPlanned && {
          endDate: endDate,
          meetPlace: meetPlace,
          budget: parseInt(budget) || 0,
          periodStartDate: periodStartDate,
          periodEndDate: periodEndDate,
        }),

      };

      // post ÏöîÏ≤≠ÏúºÎ°ú Î°úÏª¨Ìò∏Ïä§Ìä∏ 5000Î≤àÏóê Î≥¥ÎÉÑ, 2Î≤àÏß∏ Ïù∏Ïûê: Ï†ÑÏÜ°Ìï† jsonÎç∞Ïù¥ÌÑ∞, 3Î≤àÏß∏ Ïù∏Ïûê: Ïò¥ÏÖò
      const response = await axios.post(
        "http://localhost:5000/api/lists/create",
        listData,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      if(!window.confirm("Î¶¨Ïä§Ìä∏ ÏûëÏÑ±ÏùÑ ÏôÑÎ£åÌïòÏãúÍ≤†ÏäµÎãàÍπå?")){
        return;
      };

      if (response.data.success) {
        alert("Î¶¨Ïä§Ìä∏Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÎêòÏóàÏäµÎãàÎã§.");
        // Îì±Î°ù ÏÑ±Í≥µ ÌõÑ ÌôàÏúºÎ°ú Ïù¥Îèô
        window.location.href = "/home";
      }
    } catch (error) {
      console.error("Î¶¨Ïä§Ìä∏ Îì±Î°ù Ï§ë Ïò§Î•ò Î∞úÏÉù: ", error);
      alert(
        error.response?.data?.message || "Î¶¨Ïä§Ìä∏ Îì±Î°ù Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§."
      );
    }
  };

  return (
    <>
      {/*Í∞ÄÏû• Î∞îÍπ•ÏòÅÏó≠ ÌôîÎ©¥Ïùò ÏµúÎåÄÎ°ú ÏÑ§Ï†ï*/}
      <div className="grid grid-cols-1 grid-rows-9 sm:grid-rows-1 sm:grid-cols-sm-minmax sm:grid-cols-9 h-screen overflow-x-hidden">
        {/* ÏôºÏ™Ω: Í∞ÄÎ°ú 1 ÎπÑÏú® (1/9) */}
        <Menu current_src={5} />
        {/* Ïò§Î•∏Ï™Ω: Í∞ÄÎ°ú 8 ÎπÑÏú® (8/9) */}
        <div className="flex flex-col flex-wrap row-span-9 sm:col-span-8 ">
          {/* <div className="flex items-center justify-center  w-full h-full"> */}
          <div className={`w-full h-full grid grid-cols-[3fr_2fr]`}>
            {/* ÏôºÏ™Ω ÏòÅÏó≠ */}
            <div className="grid grid-rows-[1fr_7fr_1fr] px-4 py-8">
              {/* ÏÉÅÎã® ÏòÅÏó≠ */}
              <div className="flex grid grid-rows-[0.5fr_1fr]">
                <TopButtons
                  isPlanned={isPlanned}
                  setIsPlanned={setIsPlanned}
                  Group={Group}
                  setGroup={setGroup}
                  Offline={Offline}
                  setOffline={setOffline}
                />
                {/* Ï†úÎ™©, ÌîÑÎ°úÌïÑ, Í¥ÄÏã¨ÏÇ¨ ÏÑ†ÌÉù ÏòÅÏó≠ */}
                <TitleAndSelectInterest
                  selectInterest={selectInterest}
                  selectedInterest={selectedInterest}
                  setSelectedInterest={setSelectedInterest}
                  title={title}
                  setTitle={setTitle}
                  userInfo={userInfo}
                />
              </div>
              {/* Ï§ëÍ∞Ñ ÏòÅÏó≠ */}
              {/* ÏùºÏ∞® Ï∂îÍ∞Ä Î≤ÑÌäº */}
              <div className="grid grid-rows-[1fr_10fr]">
                <DayList
                  days={days}
                  setDays={setDays}
                  registeredItems={registeredItems}
                  setRegisteredItems={setRegisteredItems}
                />
              </div>
              {/* ÌïòÎã® ÏòÅÏó≠ */}
              <div className="flex items-center">
                <TagManager
                  tags={tags}
                  setTags={setTags}
                  showInput={showInput}
                  setShowInput={setShowInput}
                  currentTag={currentTag}
                  setCurrentTag={setCurrentTag}
                  editingIndex={editingIndex}
                  setEditingIndex={setEditingIndex}
                  editingTag={editingTag}
                  setEditingTag={setEditingTag}
                  handleAddTag={handleAddTag}
                  handleEditTag={handleEditTag}
                  handleDeleteTag={handleDeleteTag}
                  handleKeyPress={handleKeyPress}
                  title={title}
                  setTitle={setTitle}
                />
              </div>
            </div>
            {/* Ïò§Î•∏Ï™Ω ÏòÅÏó≠ */}
            <div className="border-l flex flex-col h-full">
              {isPlanned ? (
                // Í≥ÑÌöçÌòï: 40% (ÎßàÍ∞êÎÇ†Ïßú/ÎßåÎÇ†Ïû•ÏÜå/ÏòàÏÇ∞/Ïã§ÌñâÍ∏∞Í∞Ñ) + 50% (Í∏ÄÏûëÏÑ±) + 10% (Îì±Î°ùÌïòÍ∏∞)
                <>
                  <div className="h-[40%] flex items-center justify-center">
                    <IsPlanned
                      setEndDate={setEndDate}
                      setMeetPlace={setMeetPlace}
                      setBudget={setBudget}
                      setPeriodStartDate={setPeriodStartDate}
                      setPeriodEndDate={setPeriodEndDate}
                    />
                  </div>
                  <div className="h-[50%] flex items-center justify-center">
                    <textarea
                      className="w-full h-full border rounded m-4 p-4 resize-none"
                      placeholder="ÏÜåÍ∞ú Í∏ÄÏùÑ ÏûêÏú†Î°≠Í≤å ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                      value={text}
                      onChange={(e) => setText(e.target.value)}
                    ></textarea>
                  </div>
                  <div className="h-[10%] flex justify-end items-center">
                    <button
                      className="bg-blue-500 text-white h-10 mr-8 px-3 rounded-xl"
                      onClick={handleSubmit}
                    >
                      Îì±Î°ùÌïòÍ∏∞
                    </button>
                  </div>
                </>
              ) : (
                // ÏûêÏú†Ìòï: 90% (Í∏ÄÏûëÏÑ±) + 10% (Îì±Î°ùÌïòÍ∏∞)
                <>
                  <div className="h-[90%] flex items-center justify-center">
                    <textarea
                      className="w-full h-full border rounded p-2 resize-none"
                      placeholder="ÏÜåÍ∞ú Í∏ÄÏùÑ ÏûêÏú†Î°≠Í≤å ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                      value={text}
                      onChange={(e) => setText(e.target.value)}
                    ></textarea>
                  </div>
                  <div className="h-[10%] flex justify-end items-center">
                    <button
                      className="bg-blue-500 text-white h-10 mr-8 px-3 rounded-xl"
                      onClick={handleSubmit}
                    >
                      Îì±Î°ùÌïòÍ∏∞
                    </button>
                  </div>
                </>
              )}
            </div>
          </div>
          {/* </div> */}
        </div>
      </div>
    </>
  );
}
